import React from 'react';
import { Col, Row } from 'react-bootstrap';

const Category = ({ filterByCategory }) => {
  const onFilter = (cat) => {
    filterByCategory(cat);
  }

  return (
    <Row>
      <Col sm="12" className='d-flex justify-content-center'>
        <div>
          <button style={{ border: "1px solid green" }} className='btn mx-2' onClick={() => onFilter("All")}>All</button>
          <button style={{ border: "1px solid green" }} className='btn mx-2' onClick={() => onFilter("Dinner")}>Dinner</button>
          <button style={{ border: "1px solid green" }} className='btn mx-2' onClick={() => onFilter("Breakfast")}>Breakfast</button>
        </div>
      </Col>
    </Row>
  );
}

export default Category;
